mapper.select:
  	select user_id from client_user_lease_data_correlation t 

  		 (
		  	project_id = #{projectId,jdbcType=INTEGER}
			or t.user_id in (
				select user_id from (
		 			select user_id,count(1) as ct from client_user_lease_data_correlation 

		 				status != 'DELETE'

		 			GROUP BY user_id
				)a where a.ct = 1
			) 
		)

			and user_id 
			in 

	         #{userId}



	group by user_id desc

mapper.update:
 	update client_user_lease_data_correlation t 
	set t.status = 'DELETE'


			and t.subject_id = #{subjectId,jdbcType=BIGINT}


			and t.renter_id = #{renterId,jdbcType=BIGINT}

		and t.user_id = #{userId,jdbcType=BIGINT}

 	update client_user_lease_data_correlation t 
	set t.service_status = 'HISTORY'

		t.service_status != 'HISTORY'
		and (

			t.subject_id = #{subjectId,jdbcType=BIGINT}



				or 

			t.renter_id in 

	          #{renterId}


		)
